package ru.arkasandr.carfinesearcher.config;import com.microsoft.playwright.*;import lombok.extern.slf4j.Slf4j;import org.springframework.context.annotation.Configuration;@Slf4j@Configurationpublic class BrowserInitializeConfig {    public Browser launchBrowser(BrowserType.LaunchOptions options) {        Playwright playwright = Playwright.create();        BrowserType browserType = playwright.chromium();        Browser browser = browserType.launch(options);        return browser;    }    public void restartBrowser(BrowserContext browserContext, Browser browser) {        try {            browserContext.close();        } catch (Exception e) {            log.info("Browser closing was failed");        } finally {            browser.close();            log.info("Browser was closed");        }    }    public BrowserContext newBrowserContext(Browser browser, Browser.NewContextOptions newContextOptions) {        return browser.newContext(newContextOptions);    }    public Page page(BrowserContext browserContext) {        return browserContext.newPage();    }}