package ru.arkasandr.carfinesearcher.config.rabbitmq;import lombok.RequiredArgsConstructor;import org.springframework.amqp.core.AmqpAdmin;import org.springframework.amqp.core.Queue;import org.springframework.amqp.rabbit.connection.ConnectionFactory;import org.springframework.amqp.rabbit.core.RabbitAdmin;import org.springframework.amqp.rabbit.core.RabbitTemplate;import org.springframework.amqp.support.converter.Jackson2JsonMessageConverter;import org.springframework.amqp.support.converter.MessageConverter;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.integration.annotation.IntegrationComponentScan;import org.springframework.integration.config.EnableIntegration;@Configuration@RequiredArgsConstructor@IntegrationComponentScan@EnableIntegrationpublic class RabbitMQCaptchaRequestMessageConfig {    @Value("${spring.rabbitmq.queue_captcha}")    private String queue;    @Value("${spring.rabbitmq.exchange_captcha}")    private String exchange;    @Value("${spring.rabbitmq.routingkey_captcha}")    private String routingKey;    private final ConnectionFactory connectionFactory;    @Bean    public AmqpAdmin amqpCaptchaAdmin() {        return new RabbitAdmin(connectionFactory);    }    @Bean    public RabbitTemplate captchaRequestTemplate() {        RabbitTemplate rabbitTemplate = new RabbitTemplate(connectionFactory);        rabbitTemplate.setExchange(exchange);        rabbitTemplate.setRoutingKey(routingKey);        rabbitTemplate.setConnectionFactory(connectionFactory);        rabbitTemplate.setMessageConverter(jsonCaptchaMessageConverter());        return rabbitTemplate;    }    @Bean    Queue queueCaptchaRequest() {        return new Queue(queue, true);    }    @Bean    public MessageConverter jsonCaptchaMessageConverter() {        return new Jackson2JsonMessageConverter();    }}