package ru.arkasandr.carfinesearcher.config.rabbitmq;import lombok.RequiredArgsConstructor;import org.aopalliance.aop.Advice;import org.springframework.amqp.core.*;import org.springframework.amqp.rabbit.config.RetryInterceptorBuilder;import org.springframework.amqp.rabbit.connection.ConnectionFactory;import org.springframework.amqp.rabbit.core.RabbitTemplate;import org.springframework.amqp.rabbit.listener.SimpleMessageListenerContainer;import org.springframework.amqp.rabbit.retry.RejectAndDontRequeueRecoverer;import org.springframework.amqp.support.converter.MessageConverter;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.integration.annotation.IntegrationComponentScan;import org.springframework.integration.config.EnableIntegration;import org.springframework.retry.interceptor.RetryOperationsInterceptor;@Configuration@RequiredArgsConstructor@IntegrationComponentScan@EnableIntegrationpublic class RabbitMQCaptchaRequestWithValueMessageConfig {    @Value("${spring.rabbitmq.queue_captcha_value}")    private String queue;    @Value("${spring.rabbitmq.exchange_captcha_value}")    private String exchange;    @Value("${spring.rabbitmq.routingkey_captcha_value}")    private String routingKey;    private final ConnectionFactory connectionFactory;    private final MessageConverter jsonMessageConverter;    @Bean    Queue captchaRequestWithValueQueue() {        return new Queue(queue, true);    }    @Bean    Exchange captchaRequestWithValueExchange() {        return ExchangeBuilder.directExchange(exchange)                .durable(true)                .build();    }    @Bean    Binding captchaRequestWithValueBinding() {        return BindingBuilder                .bind(captchaRequestWithValueQueue())                .to(captchaRequestWithValueExchange())                .with(routingKey)                .noargs();    }    @Bean    public RabbitTemplate captchaRequestWithValueTemplate(ConnectionFactory connectionFactory) {        final RabbitTemplate rabbitTemplate = new RabbitTemplate(connectionFactory);        rabbitTemplate.setMessageConverter(jsonMessageConverter);        return rabbitTemplate;    }    @Bean    public SimpleMessageListenerContainer captchaRequestWithValueListenerContainer() {        SimpleMessageListenerContainer container =                new SimpleMessageListenerContainer(connectionFactory);        container.setQueues(captchaRequestWithValueQueue());        container.setConcurrentConsumers(2);        container.setDefaultRequeueRejected(false);        container.setAdviceChain(new Advice[]{captchaRequestWithValueInterceptor()});        return container;    }    @Bean    RetryOperationsInterceptor captchaRequestWithValueInterceptor() {        return RetryInterceptorBuilder.stateless()                .maxAttempts(1)                .backOffOptions(1000, 1, 60000)                .recoverer(new RejectAndDontRequeueRecoverer())                .build();    }}