package ru.arkasandr.carfinesearcher.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.*;import ru.arkasandr.carfinesearcher.controller.dto.ChatDto;import ru.arkasandr.carfinesearcher.controller.mapper.ChatMapper;import ru.arkasandr.carfinesearcher.service.ChatService;import ru.arkasandr.carfinesearcher.service.JobService;@Slf4j@RestController@RequiredArgsConstructor@RequestMapping({"/api/admin"})public class AdminController {    public static final ChatMapper CHAT_MAPPER = ChatMapper.INSTANCE;    private final ChatService chatService;    private final JobService jobService;    @PostMapping("/request/max")    ChatDto changeChatMaxRequestAttempt(@RequestBody ChatDto chatDto) {        log.debug("AdminController.changeChatMaxRequestAttempt request JSON: {}", chatDto);        var chat = CHAT_MAPPER.toEntity(chatDto);        return CHAT_MAPPER.toDto(chatService.changeChatMaxRequestAttempt(chat));    }    @GetMapping("/job/archived")    void startArchivedJob() {        log.debug("AdminController.startArchivedJob");        jobService.archiveRequests();    }}