package ru.arkasandr.carfinesearcher.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import ru.arkasandr.carfinesearcher.controller.dto.AnswerDto;import ru.arkasandr.carfinesearcher.controller.dto.UserDataDto;import ru.arkasandr.carfinesearcher.mapper.AnswerMapper;import ru.arkasandr.carfinesearcher.mapper.UserDataMapper;import ru.arkasandr.carfinesearcher.model.UserData;import ru.arkasandr.carfinesearcher.service.GibddScrapService;@Slf4j@RestController@RequiredArgsConstructor@RequestMapping({"/api"})public class ScrapController {    private static final UserDataMapper USER_DATA_MAPPER = UserDataMapper.INSTANCE;    private static final AnswerMapper ANSWER_MAPPER = AnswerMapper.INSTANCE;    private final GibddScrapService scrapService;    @PostMapping("/scrap")    AnswerDto startScrapService(@RequestBody UserDataDto dataDto) {        log.debug("ScrapController.startScrapService request JSON: {}", dataDto);        UserData data = USER_DATA_MAPPER.toEntity(dataDto);        return ANSWER_MAPPER.toDto(scrapService.scrap(data));    }}