package ru.arkasandr.carfinesearcher.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import ru.arkasandr.carfinesearcher.controller.dto.AnswerDto;import ru.arkasandr.carfinesearcher.mapper.AnswerMapper;import ru.arkasandr.carfinesearcher.model.UserData;import ru.arkasandr.carfinesearcher.service.GibddScrapService;import ru.arkasandr.carfinesearcher.service.message.dto.GibddRequestMessageDto;@Slf4j@RestController@RequiredArgsConstructor@RequestMapping({"/api"})public class ScrapController {    private static final AnswerMapper ANSWER_MAPPER = AnswerMapper.INSTANCE;    private final GibddScrapService scrapService;    @PostMapping("/scrap")    AnswerDto startScrapService(@RequestBody GibddRequestMessageDto dataDto) {        log.debug("ScrapController.startScrapService request JSON: {}", dataDto);        return ANSWER_MAPPER.toDto(scrapService.scrap(dataDto));    }}