package ru.arkasandr.carfinesearcher.controller;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import ru.arkasandr.carfinesearcher.service.GibddScrapService;import ru.arkasandr.carfinesearcher.service.message.dto.Answer;import ru.arkasandr.carfinesearcher.service.message.dto.CaptchaMessageRequestDto;import ru.arkasandr.carfinesearcher.service.message.dto.CaptchaMessageResponseDto;import ru.arkasandr.carfinesearcher.service.message.dto.GibddRequestMessageDto;@Slf4j@RestController@RequiredArgsConstructor@RequestMapping({"/api"})public class ScrapController {    private final GibddScrapService scrapService;    @PostMapping("/captcha")    CaptchaMessageRequestDto scrapCaptcha(@RequestBody GibddRequestMessageDto dataDto) {        log.debug("ScrapController.scrapCaptcha request JSON: {}", dataDto);        return scrapService.scrapCaptcha(dataDto);    }    @PostMapping("/fines")    Answer scrapFines(@RequestBody CaptchaMessageResponseDto dataDto) {        log.debug("ScrapController.scrapFines request JSON: {}", dataDto);        return scrapService.scrapFines(dataDto);    }}