package ru.arkasandr.carfinesearcher.service.message;import org.springframework.integration.annotation.Gateway;import org.springframework.integration.annotation.MessagingGateway;import org.springframework.messaging.Message;import org.springframework.stereotype.Component;@MessagingGateway@Componentpublic interface MessageGateway {    @Gateway(requestChannel = "sendGibddCarDataChannel")    void sendGibddRequestWithCarData(Message<?> content);    @Gateway(requestChannel = "sendCaptchaRequestChannel")    void sendCaptchaRequestToUser(Message<?> content);    @Gateway(requestChannel = "sendCaptchaResponseWithValueChannel")    void sendCaptchaResponseWithValue(Message<?> content);    @Gateway(requestChannel = "sendAnswerToUserChannel")    void sendAnswerToUser(Message<?> content);}