package ru.arkasandr.carfinesearcher.service.message;import org.springframework.integration.annotation.Gateway;import org.springframework.integration.annotation.MessagingGateway;import org.springframework.messaging.Message;import org.springframework.stereotype.Component;@MessagingGateway@Componentpublic interface MessageGateway {    @Gateway(requestChannel = "clientChannel")    void send(Message<?> content);}