package ru.arkasandr.carfinesearcher.service.message;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.messaging.Message;import org.springframework.messaging.support.MessageBuilder;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.UUID;@RequiredArgsConstructor@Service@Slf4jpublic class MessageService {    private final MessageGateway gateway;    /**     * Метод отправляет запрос на получение данных в очередь     */    @Transactional    public void sendMessageToQueue(Long requestId) {        String mesUuid = UUID.randomUUID().toString();        Message<String> message = MessageBuilder.withPayload(String.valueOf(requestId))                .setHeader("mes_uuid", mesUuid)                .build();        gateway.generate(message);        log.info("Request message with UUID = " + mesUuid + " was sent to queue");    }}