package ru.arkasandr.carfinesearcher.service.message;import com.fasterxml.jackson.core.JsonProcessingException;import com.fasterxml.jackson.databind.ObjectMapper;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.apache.commons.io.FileUtils;import org.springframework.beans.factory.annotation.Value;import org.springframework.core.io.ByteArrayResource;import org.springframework.http.HttpEntity;import org.springframework.http.HttpHeaders;import org.springframework.http.HttpMethod;import org.springframework.http.MediaType;import org.springframework.messaging.Message;import org.springframework.messaging.support.MessageBuilder;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import org.springframework.util.LinkedMultiValueMap;import org.springframework.web.client.RestTemplate;import ru.arkasandr.carfinesearcher.exception.TelegramFileUploadException;import ru.arkasandr.carfinesearcher.model.Car;import ru.arkasandr.carfinesearcher.repository.ChatRepository;import ru.arkasandr.carfinesearcher.repository.GibddRequestRepository;import ru.arkasandr.carfinesearcher.service.GibddScrapService;import ru.arkasandr.carfinesearcher.service.message.dto.Answer;import ru.arkasandr.carfinesearcher.service.message.dto.CaptchaMessageRequestDto;import ru.arkasandr.carfinesearcher.service.message.dto.CaptchaMessageResponseDto;import ru.arkasandr.carfinesearcher.service.message.dto.GibddRequestMessageDto;import javax.persistence.EntityNotFoundException;import java.io.File;import java.io.IOException;import java.nio.file.Files;import java.nio.file.Path;import java.text.MessageFormat;import java.time.LocalDate;import java.util.Objects;import java.util.stream.Collectors;import java.util.stream.Stream;import static java.time.LocalDateTime.now;import static org.apache.commons.lang3.StringUtils.isNotBlank;import static org.apache.commons.lang3.math.NumberUtils.toLong;import static ru.arkasandr.carfinesearcher.model.enums.RequestStatus.*;@RequiredArgsConstructor@Service@Slf4jpublic class MessageService {    public static final String SEND_PHOTO_KEY = "photo";    public static final String SEND_MESSAGE_KEY = "text";    public static final String PHOTO_NAME = "captcha";    public static final String PHOTO_EXT = ".png";    @Value("${telegram.token}")    String botToken;    @Value("${telegram.url}")    String url;    private final MessageGateway gateway;    private final GibddScrapService scrapService;    private final RestTemplate restTemplate;    private final ChatRepository chatRepository;    private final GibddRequestRepository gibddRequestRepository;    /**     * Метод отправляет запрос c данными о ТС в очередь для передачи парсеру (выполняется на стороне бота)     */    @Transactional    public void sendMessageToQueueWithCarData(Car car) {        var request = car.getRequest().stream()                .findFirst()                .orElseThrow(() -> new IllegalArgumentException("Запрос должен быть найден!"));        var dto = GibddRequestMessageDto.builder()                .registrationNumber(car.getRegistrationNumber())                .certificateNumber(car.getCertificateNumber())                .uuid(request.getId())                .build();        var message = MessageBuilder                .withPayload(dto)                .build();        gateway.sendGibddRequestWithCarData(message);        log.info("Request message UUID = {} was sent to queue", request.getId());        request.setStatus(SENDING);        request.setRequestDate(now());        gibddRequestRepository.save(request);    }    /**     * Метод получает данные о ТС для парсера из очереди (выполняется на стороне парсера)     */    public void receiveMessageFromQueueWithCarData(Message<String> message) {        String messagePayload = message.getPayload();        ObjectMapper mapper = new ObjectMapper();        try {            GibddRequestMessageDto dto = mapper.readValue(messagePayload, GibddRequestMessageDto.class);            log.info("Response message with UUID = {} was received from queue", dto.getUuid().toString());            var captchaMessageRequest = scrapService.scrapCaptcha(dto);            sendCaptchaRequestToUser(captchaMessageRequest);            log.info("Response message UUID = {} with captcha was send to user", captchaMessageRequest.getUuid().toString());        } catch (JsonProcessingException e) {            e.printStackTrace();        }    }    /**     * Метод отправляет запрос в очередь c captcha для распознования пользователем (выполняется на стороне парсера)     */    public void sendCaptchaRequestToUser(CaptchaMessageRequestDto captchaMessageRequestDto) {        var message = MessageBuilder                .withPayload(captchaMessageRequestDto)                .build();        gateway.sendCaptchaRequestToUser(message);        log.info("Request message UUID = {} was sent to queue", captchaMessageRequestDto.getUuid());    }    /**     * Метод получает запрос из очереди c captcha для распознования пользователем (выполняется на стороне бота)     */    @Transactional    public void receiveMessageFromQueueWithCaptcha(Message<String> message) {        String messagePayload = message.getPayload();        ObjectMapper mapper = new ObjectMapper();        try {            CaptchaMessageRequestDto dto = mapper.readValue(messagePayload, CaptchaMessageRequestDto.class);            log.info("Response message UUID = {} was received from queue", dto.getUuid().toString());            var chat = chatRepository.findChatByRequestUUID(dto.getUuid())                    .orElseThrow(EntityNotFoundException::new);            byte[] content = dto.getCaptcha();            LinkedMultiValueMap<String, Object> map = new LinkedMultiValueMap<>();            map.add(SEND_PHOTO_KEY, createPhotoResource(content, PHOTO_NAME, PHOTO_EXT));            map.add("caption", "Введите цифры");            HttpHeaders headers = new HttpHeaders();            headers.setContentType(MediaType.MULTIPART_FORM_DATA);            HttpEntity<LinkedMultiValueMap<String, Object>> requestEntity = new HttpEntity<>(map, headers);            restTemplate.exchange(                    MessageFormat.format("{0}bot{1}/sendPhoto?chat_id={2}",                            url, botToken, chat.getChatId().toString()),                    HttpMethod.POST,                    requestEntity,                    String.class);            log.info("Captcha picture message UUID = {} was send to User", dto.getUuid().toString());            var request = gibddRequestRepository.findById(dto.getUuid())                    .orElseThrow(() -> new EntityNotFoundException("Запрос с UUID = " + dto.getUuid() + " отсутствует!"));            request.setStatus(CAPTCHA_IS_WAITING);            request.setCaptchaPic(content);            gibddRequestRepository.save(request);        } catch (Exception e) {            throw new TelegramFileUploadException();        }    }    /**     * Метод отправляет запрос cо значением captcha в очередь для передачи парсеру (выполняется на стороне бота)     */    @Transactional    public void sendMessageToQueueWithCaptchaValue(Car car, String captcha) {        var request = car.getRequest().stream()                .findFirst()                .orElseThrow(() -> new IllegalArgumentException("Запрос должен быть найден!"));        var dto = CaptchaMessageResponseDto.builder()                .uuid(request.getId())                .value(captcha)                .build();        var message = MessageBuilder                .withPayload(dto)                .build();        gateway.sendCaptchaResponseWithValue(message);        log.info("CaptchaMessageResponse message UUID = {} was sent to queue with captcha value", request.getId());        request.setStatus(CAPTCHA_SENT);        request.setCaptchaCode(toLong(captcha));        gibddRequestRepository.save(request);    }    /**     * Метод получает captcha с распознанным значением для парсера из очереди (выполняется на стороне парсера)     */    public void receiveMessageFromQueueWithCaptchaValue(Message<String> message) {        String messagePayload = message.getPayload();        ObjectMapper mapper = new ObjectMapper();        try {            CaptchaMessageResponseDto dto = mapper.readValue(messagePayload, CaptchaMessageResponseDto.class);            log.info("CaptchaMessageResponse message with UUID = {} was received from queue", dto.getUuid().toString());            var answer = scrapService.scrapFines(dto);            sendFinesToUser(answer);        } catch (JsonProcessingException e) {            e.printStackTrace();        }    }    /**     * Метод отправляет в очередь ответ с информацией о штрафах (выполняется на стороне парсера)     */    public void sendFinesToUser(Answer userAnswer) {        var message = MessageBuilder                .withPayload(userAnswer)                .build();        gateway.sendAnswerToUser(message);        log.info("Response message UUID = {} with user's fines was sent to queue", userAnswer.getUuid());    }    /**     * Метод получает ответ из очереди с информацией о выполненном запросе (выполняется на стороне бота)     */    public void receiveAnswerToUserFromQueue(Message<String> message) {        String messagePayload = message.getPayload();        ObjectMapper mapper = new ObjectMapper();        try {            Answer dto = mapper.readValue(messagePayload, Answer.class);            log.info("AnswerToUserResponse message with UUID = {} was received from queue", dto.getUuid().toString());            var userMessage = createMessageFromAnswer(dto);            var chat = chatRepository.findChatByRequestUUID(dto.getUuid())                    .orElseThrow(EntityNotFoundException::new);            LinkedMultiValueMap<String, Object> map = new LinkedMultiValueMap<>();            map.add(SEND_MESSAGE_KEY, userMessage);            HttpHeaders headers = new HttpHeaders();            HttpEntity<LinkedMultiValueMap<String, Object>> requestEntity = new HttpEntity<>(map, headers);            restTemplate.exchange(                    MessageFormat.format("{0}bot{1}/sendMessage?chat_id={2}",                            url, botToken, chat.getChatId().toString()),                    HttpMethod.POST,                    requestEntity,                    String.class);            log.info("Answer with fines message UUID = {} was send to User", dto.getUuid().toString());            var request = gibddRequestRepository.findById(dto.getUuid())                    .orElseThrow(() -> new EntityNotFoundException("Запрос с UUID = " + dto.getUuid() + " отсутствует!"));            request.setStatus(EXECUTED);            gibddRequestRepository.save(request);        } catch (Exception e) {            e.printStackTrace();        }    }    private String createMessageFromAnswer(Answer answer) {        var checkingInfo = isNotBlank(answer.getCheckingInfo())                ? ("Информация: ").concat(answer.getCheckingInfo())                : null;        var checkingMessage = isNotBlank(answer.getCheckingMessage())                ? ("Результат проверки: ").concat(answer.getCheckingMessage())                : null;        var checkingDate = isNotBlank(answer.getCheckingDate())                ? answer.getCheckingDate()                : ("Дата проверки: ").concat(LocalDate.now().toString());        return Stream.of(checkingInfo, checkingMessage, checkingDate)                .filter(Objects::nonNull)                .collect(Collectors.joining("; ", "",  "\n"));    }    /**     * Использование ByteArrayResource делает обязательным переопределение метода String getFilename()     */    public static ByteArrayResource createPhotoResource(byte[] photo, String name, String suffix) throws IOException {        return new ByteArrayResource(Files.readAllBytes(createOfficeDocumentFile(photo, name, suffix))) {            @Override            public String getFilename() {                return MessageFormat.format("{0}.{1}", name, suffix);            }        };    }    private static Path createOfficeDocumentFile(byte[] photo, String name, String suffix) throws IOException {        File file = File.createTempFile(name, suffix);        FileUtils.writeByteArrayToFile(file, photo);        return file.toPath();    }}