package ru.arkasandr.carfinesearcher.service.util;import java.util.AbstractMap;import java.util.Map;import java.util.Objects;import java.util.stream.Collectors;public class CharTransformUtil {    public static final Map<Character, Character> LATIN_TO_CYRILLIC = Map.ofEntries(            new AbstractMap.SimpleEntry<>('A', 'А'),            new AbstractMap.SimpleEntry<>('B', 'В'),            new AbstractMap.SimpleEntry<>('C', 'С'),            new AbstractMap.SimpleEntry<>('E', 'Е'),            new AbstractMap.SimpleEntry<>('H', 'Н'),            new AbstractMap.SimpleEntry<>('K', 'К'),            new AbstractMap.SimpleEntry<>('M', 'М'),            new AbstractMap.SimpleEntry<>('O', 'О'),            new AbstractMap.SimpleEntry<>('P', 'Р'),            new AbstractMap.SimpleEntry<>('T', 'Т'),            new AbstractMap.SimpleEntry<>('Y', 'У'),            new AbstractMap.SimpleEntry<>('X', 'Х'),            new AbstractMap.SimpleEntry<>('0', '0'),            new AbstractMap.SimpleEntry<>('1', '1'),            new AbstractMap.SimpleEntry<>('2', '2'),            new AbstractMap.SimpleEntry<>('3', '3'),            new AbstractMap.SimpleEntry<>('4', '4'),            new AbstractMap.SimpleEntry<>('5', '5'),            new AbstractMap.SimpleEntry<>('6', '6'),            new AbstractMap.SimpleEntry<>('7', '7'),            new AbstractMap.SimpleEntry<>('8', '8'),            new AbstractMap.SimpleEntry<>('9', '9')    );    public static final Map<Character, Character> CYRILLIC_TO_LATIN = Map.ofEntries(            new AbstractMap.SimpleEntry<>('А', 'A'),            new AbstractMap.SimpleEntry<>('В', 'B'),            new AbstractMap.SimpleEntry<>('С', 'C'),            new AbstractMap.SimpleEntry<>('Е', 'E'),            new AbstractMap.SimpleEntry<>('Н', 'H'),            new AbstractMap.SimpleEntry<>('К', 'K'),            new AbstractMap.SimpleEntry<>('М', 'M'),            new AbstractMap.SimpleEntry<>('О', 'O'),            new AbstractMap.SimpleEntry<>('Р', 'P'),            new AbstractMap.SimpleEntry<>('Т', 'T'),            new AbstractMap.SimpleEntry<>('У', 'Y'),            new AbstractMap.SimpleEntry<>('Х', 'X'),            new AbstractMap.SimpleEntry<>('0', '0'),            new AbstractMap.SimpleEntry<>('1', '1'),            new AbstractMap.SimpleEntry<>('2', '2'),            new AbstractMap.SimpleEntry<>('3', '3'),            new AbstractMap.SimpleEntry<>('4', '4'),            new AbstractMap.SimpleEntry<>('5', '5'),            new AbstractMap.SimpleEntry<>('6', '6'),            new AbstractMap.SimpleEntry<>('7', '7'),            new AbstractMap.SimpleEntry<>('8', '8'),            new AbstractMap.SimpleEntry<>('9', '9')    );    public static String latinToCyrillic(String inputString) {        if (!inputString.matches(".*[а-яА-Я]+.*")) {            return inputString.toUpperCase().chars()                    .mapToObj(c -> (char) c)                    .map(LATIN_TO_CYRILLIC::get)                    .map(Objects::toString)                    .collect(Collectors.joining());        }        return inputString.toUpperCase();    }    public static String cyrillicToLatin(String inputString) {        if (!inputString.matches(".*[a-zA-Z]+.*")) {            return inputString.toUpperCase().chars()                    .mapToObj(c -> (char) c)                    .map(CYRILLIC_TO_LATIN::get)                    .map(Objects::toString)                    .collect(Collectors.joining());        }        return inputString.toUpperCase();    }}