package ru.arkasandr.carfinesearcher.service.util;import org.telegram.telegrambots.meta.api.methods.send.SendMessage;import org.telegram.telegrambots.meta.api.objects.replykeyboard.ReplyKeyboardMarkup;public class GenerateSendMessageUtil {    public static SendMessage generateSendMessageWithKeyboard(String chatId, String message,                                                              ReplyKeyboardMarkup keyboardMarkup) {        SendMessage sendMessage = new SendMessage(chatId, message);        sendMessage.enableMarkdown(true);        sendMessage.setReplyMarkup(keyboardMarkup);        sendMessage.enableHtml(true);        return sendMessage;    }}