package ru.arkasandr.carfinesearcher.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import java.util.HashSet;import java.util.Set;import static java.time.LocalDateTime.now;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.assertj.core.api.Assertions.*;@DisplayName("Класс Car")class CarTest {    @Test    @DisplayName("новый car корректно создаётся")    void haveCorrectConstructorWithAllArguments() {        Long id = 1L;        String regNumber = "A999AA555";        String certNumber = "11AA123123";        Chat chat = Chat.builder()                .chatId(1L)                .firstName("firstName")                .userName("userName")                .build();        Set<Fine> carFines = new HashSet<>();        GibddRequest request = GibddRequest.builder()                .id(1L)                .createDate(now())                .build();        Car testCar = Car.builder()                .id(id)                .registrationNumber(regNumber)                .certificateNumber(certNumber)                .chat(chat)                .carFines(carFines)                .request(request)                .build();        assertEquals("A999AA555", testCar.getRegistrationNumber());        assertThat(testCar)                .hasFieldOrPropertyWithValue("certificateNumber", "11AA123123");    }}