package ru.arkasandr.carfinesearcher.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import java.util.HashSet;import java.util.Set;import java.util.UUID;import static java.time.LocalDateTime.now;import static org.assertj.core.api.Assertions.assertThat;import static org.junit.jupiter.api.Assertions.assertEquals;@DisplayName("Класс Car")class CarTest {    @Test    @DisplayName("новый car корректно создаётся")    void haveCorrectConstructorWithAllArguments() {        Long id = 1L;        UUID uuid = UUID.fromString("73f17c7d-b897-443e-8181-ffadaa9bb36f");        String regNumber = "A999AA555";        String certNumber = "11AA123123";        Chat chat = Chat.builder()                .chatId(id)                .firstName("firstName")                .userName("userName")                .build();        Set<Fine> carFines = new HashSet<>();        GibddRequest request = GibddRequest.builder()                .id(uuid)                .createDate(now())                .build();        Car testCar = Car.builder()                .id(id)                .registrationNumber(regNumber)                .certificateNumber(certNumber)                .updateDate(now())                .chat(chat)                .carFines(carFines)                .request(Set.of(request))                .build();        assertEquals("A999AA555", testCar.getRegistrationNumber());        assertThat(testCar)                .hasFieldOrPropertyWithValue("certificateNumber", "11AA123123");    }}