package ru.arkasandr.carfinesearcher.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import static org.assertj.core.api.Assertions.assertThat;import static org.junit.jupiter.api.Assertions.*;@DisplayName("Класс Chat")class ChatTest {    @Test    @DisplayName("новый chat корректно создаётся")    void haveCorrectConstructorWithAllArguments() {        Long id = 1L;        Long chatId = 22L;        String firstName = "firstName";        String lastName = "lastName";        String userName = "userName";        Chat testChat = Chat.builder()                .id(id)                .chatId(chatId)                .firstName(firstName)                .lastName(lastName)                .userName(userName)                .build();        assertEquals("firstName", testChat.getFirstName());        assertThat(testChat)                .hasFieldOrPropertyWithValue("userName", "userName");    }}