package ru.arkasandr.carfinesearcher.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import ru.arkasandr.carfinesearcher.model.enums.RequestStatus;import java.time.LocalDateTime;import static org.assertj.core.api.Assertions.assertThat;import static org.junit.jupiter.api.Assertions.*;@DisplayName("Класс GibddRequest")class GibddRequestTest {    @Test    @DisplayName("новый gibddRequest корректно создаётся")    void haveCorrectConstructorWithAllArguments() {        Long id = 1L;        LocalDateTime createDate = LocalDateTime.of(2022, 7, 12, 9, 20, 30);        LocalDateTime requestDate = LocalDateTime.of(2022, 7, 13, 10, 02, 51);        LocalDateTime responseDate = LocalDateTime.of(2022, 7, 13, 10, 15, 04);        GibddRequest testGibddRequest = GibddRequest.builder()                .id(id)                .createDate(createDate)                .requestDate(requestDate)                .responseDate(responseDate)                .status(RequestStatus.READY_FOR_SEND)                .build();        assertEquals("2022-07-12T09:20:30", testGibddRequest.getCreateDate().toString());        assertThat(testGibddRequest)                .hasFieldOrPropertyWithValue("id", 1L);    }}