package ru.arkasandr.carfinesearcher.model;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import ru.arkasandr.carfinesearcher.model.enums.RequestStatus;import java.time.LocalDateTime;import java.util.UUID;import static org.assertj.core.api.Assertions.assertThat;import static org.junit.jupiter.api.Assertions.assertEquals;@DisplayName("Класс GibddRequest")class GibddRequestTest {    @Test    @DisplayName("новый gibddRequest корректно создаётся")    void haveCorrectConstructorWithAllArguments() {        UUID id = UUID.fromString("73f17c7d-b897-443e-8181-ffadaa9bb36f");        LocalDateTime createDate = LocalDateTime.of(2022, 7, 12, 9, 20, 30);        LocalDateTime requestDate = LocalDateTime.of(2022, 7, 13, 10, 02, 51);        LocalDateTime responseDate = LocalDateTime.of(2022, 7, 13, 10, 15, 04);        GibddRequest testGibddRequest = GibddRequest.builder()                .id(id)                .createDate(createDate)                .requestDate(requestDate)                .responseDate(responseDate)                .status(RequestStatus.READY_FOR_SEND)                .build();        assertEquals("2022-07-12T09:20:30", testGibddRequest.getCreateDate().toString());        assertThat(testGibddRequest)                .hasFieldOrPropertyWithValue("id", id);    }}