package ru.arkasandr.carfinesearcher.service;import org.junit.jupiter.api.Test;import org.telegram.telegrambots.meta.api.methods.send.SendMessage;import static org.assertj.core.api.Assertions.assertThat;import static ru.arkasandr.carfinesearcher.telegram.constants.BotMessageEnum.EXCEPTION_EMPTY_MESSAGE;class ValidateDataServiceTest {    private ValidateDataService underTest = new ValidateDataService();    @Test    void emptyExceptionWhenEmptyUserData() {        String userData = "";        String chatId = "1";        assertThat(underTest.validateUserData(chatId, userData))                .isEqualTo(new SendMessage(chatId, EXCEPTION_EMPTY_MESSAGE.getMessage()));    }}